import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App(props) {
  const [isToggled, setIsToggled] = useState(false);

  const toggleState = () =>{
    if (isToggled) {
      setIsToggled(false);
      console.log('state changed to false');
    }
    else{
      setIsToggled(true);
      console.log('state changed to true');
    }
  }



  return (
    <div className="App">
      <button onClick={toggleState} title={isToggled} >
          click to Toggle state
      </button>
      <h1>
      {isToggled ? "Toggle On" : "Toggle Off"}
      </h1>
    </div>
  );
}

export default App;
